package com.rarible.protocol.nft.core.service.token.filter

import com.rarible.protocol.nft.core.model.FeatureFlags
import com.rarible.protocol.nft.core.repository.token.TokenByteCodeRepository
import io.daonomic.rpc.domain.Binary
import io.daonomic.rpc.domain.Word
import io.mockk.every
import io.mockk.mockk
import org.assertj.core.api.Assertions.assertThat
import org.junit.jupiter.api.Test
import scalether.util.Hash

internal class ScamByteCodeHashFilterTest {
    private val scamCode = Binary.apply(
        ""
    )

    private val featureFlags = mockk<FeatureFlags> {
        every { filterScamToken } returns true
    }
    private val tokenByteCodeRepository = mockk<TokenByteCodeRepository>()
    private val scamByteCodeHashCache = ScamByteCodeHashCache(tokenByteCodeRepository).also {
        it.add(Word.apply("0xc280308e8ee9552d68d2e83929085d45086ba9d32782458d67209189237798c9"))
    }
    private val filter = ScamByteCodeHashFilter(featureFlags, scamByteCodeHashCache)

    @Test
    fun `found scam`() {
        val resul = filter.isValid(scamCode, Word.apply(Hash.sha3(scamCode.bytes())))
        assertThat(resul).isFalse
    }

    @Test
    fun `not scam`() {
        val code =
            Binary.apply("0x608060405236600a57005b600036606060008073a281c0d1dce3ff738bbb5aa6849c82f2a3e564936001")
        val resul = filter.isValid(code, Word.apply(Hash.sha3(code.bytes())))
        assertThat(resul).isTrue
    }
}
